import { Pipe, PipeTransform } from "@angular/core";
import { MediaType } from "@memebox/contracts";

@Pipe({
  name: "mediaTypeClass",
  pure: true,
})
export class MediaTypeClassPipe implements PipeTransform {
  transform(value: MediaType): string {
    switch (value) {
      case MediaType.Picture:
        return "image";
      case MediaType.Video:
        return "video";
      case MediaType.Audio:
        return "audio";
      case MediaType.IFrame:
        return "iframe";
      default:
        return "";
    }
  }
}

import {Pipe, PipeTransform} from '@angular/core';
import {MediaType} from "@memebox/contracts";

@Pipe({
  name: 'mediaType',
  pure: true
})
export class MediaTypePipe implements PipeTransform {

  transform(value: MediaType): string {
    switch (value) {
      case MediaType.Picture:
        return 'Image';
      case MediaType.Video:
        return 'Video';
      case MediaType.Audio:
        return 'Audio';
      default:
        return '';
    }
  }

}

import { Pipe, PipeTransform } from "@angular/core";
import { DomSanitizer, SafeResourceUrl } from "@angular/platform-browser";

/**
 * We need to sanitize a url for a IFrame to avoid unsafe value error.
 * more info : https://stackoverflow.com/questions/38037760/how-to-set-iframe-src-without-causing-unsafe-value-exception
 */
@Pipe({ name: "safeurl" })
export class SafePipe implements PipeTransform {
  constructor(private sanitizer: DomSanitizer) {}
  transform(url: string): SafeResourceUrl {
    return this.sanitizer.bypassSecurityTrustResourceUrl(url);
  }
}

