import {Component, Inject, OnDestroy, OnInit, TrackByFunction} from '@angular/core';
import {MAT_DIALOG_DATA} from "@angular/material/dialog";
import {Clip, Dictionary, Screen} from "@memebox/contracts";
import {Observable, Subject} from "rxjs";
import {MatSelectionListChange} from "@angular/material/list";
import {takeUntil} from "rxjs/operators";
import {AppQueries} from "../../../../../state/app.queries";
import {AppService} from "../../../../../state/app.service";

@Component({
  selector: 'app-clip-assigning-dialog',
  templateUrl: './screen-assigning-dialog.component.html',
  styleUrls: ['./screen-assigning-dialog.component.css']
})
export class ScreenAssigningDialogComponent implements OnInit, OnDestroy {

  checkedMap: Dictionary<boolean>;
  screenList: Observable<Screen[]> = this.appQueries.screensList$;
  private destroy$ = new Subject();

  constructor(@Inject(MAT_DIALOG_DATA) public data: Clip,
              private appQueries: AppQueries,
              private appService: AppService) {
  }

  trackByScreen: TrackByFunction<Screen> = (index, item) => {
    return item.id;
  }

  ngOnInit(): void {
    this.screenList.pipe(
      takeUntil(this.destroy$),
    ).subscribe(allUrls => {
      this.checkedMap = {};

      allUrls.forEach(url => {
        if (url.clips[this.data.id]) {
          this.checkedMap[url.id] = true;
        }
      });
    })
  }

  onSelectionChanged($event: MatSelectionListChange) {
    const {value, selected} = $event.option;

    if (selected) {
      this.appService.addOrUpdateScreenClip(value, {
        id: this.data.id,
      });
    } else {
      this.appService.deleteScreenClip(value, this.data.id);
    }
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}

import {Component, Inject, OnInit} from '@angular/core';
import {AppQueries} from "../../../../state/app.queries";
import {Observable, Subject} from "rxjs";
import {PositionEnum, ScreenClip} from "@memebox/contracts";
import {map, takeUntil} from "rxjs/operators";
import {MAT_DIALOG_DATA, MatDialogRef} from "@angular/material/dialog";
import {FormBuilder} from "@angular/forms";
import {AppService} from "../../../../state/app.service";
import {SnackbarService} from "../../../../core/services/snackbar.service";

export interface ScreenClipOptionsPayload {
  screenId: string;
  clipId: string;
  name: string;
}

@Component({
  selector: 'app-screen-clip-options',
  templateUrl: './screen-clip-options.component.html',
  styleUrls: ['./screen-clip-options.component.scss']
})
export class ScreenClipOptionsComponent implements OnInit {

  public form = new FormBuilder().group({
    id: '',
    width: '',
    height: '',

    position: PositionEnum.FullScreen,
    left: '',
    right: '',
    bottom: '',
    top: '',
    imgFit: '', // todo image fit setting as enum
  })

  public clipInfo$: Observable<ScreenClip> = this.appQueries.screenMap$.pipe(
    map(screenMap => screenMap[this.data.screenId].clips[this.data.clipId])
  );
  private _clipInfo: ScreenClip = null;
  private destroy$ = new Subject();

  constructor(@Inject(MAT_DIALOG_DATA) public data: ScreenClipOptionsPayload,
              private dialogRef: MatDialogRef<any>,
              private appQueries: AppQueries,
              private appService: AppService,
              private snackBar: SnackbarService) {
  }

  ngOnInit(): void {
    this.clipInfo$.pipe(
      takeUntil(this.destroy$)
    ).subscribe(value => {
      this._clipInfo = {
        position: PositionEnum.FullScreen,
        ...value
      };

      this.form.reset(this._clipInfo);
    })
  }

  async save() {
    if (!this.form.valid) {
      // highlight hack
      this.form.markAllAsTouched();
      return;
    }

    const {value} = this.form;

    const newScreenClipValue: ScreenClip = {
      ...this._clipInfo,
      ...value
    };

    await this.appService.addOrUpdateScreenClip(this.data.screenId, newScreenClipValue);

    // todo refactor "better way?" to trigger those snackbars
    this.snackBar.normal(`Screen / Clip Assignment updated ðŸŽ‰`);

    this.dialogRef.close();
  }
}

