      // highlight hack
      this.form.markAllAsTouched();
      return;
    }

    this.editMode = false;
    await this.appService.updateTwitchChannel(this.form.value.name);
  }

  toggleOrSave() {
    if (this.editMode) {
      this.save();
    } else {
      this.editMode = true;
    }
  }
}

